import React, { useState } from 'react';
import axios from 'axios';

function AddIssue({ addItem }) {
    const [issue, setIssue] = useState({ vehicle: '', component: '', type: 'repair', quantity: 1 });
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Add item to payment
            addItem({ 
                name: issue.component,
                quantity: issue.quantity,
                newPrice: 100, // Replace with actual price or logic
                repairPrice: 50 // Replace with actual price or logic
            });

            // Optionally reset the form after submission
            setIssue({ vehicle: '', component: '', type: 'repair', quantity: 1 });
            setMessage('Issue added successfully!');
        } catch (error) {
            console.error('There was an error adding the issue!', error);
            setMessage('Failed to add issue. Please try again.');
        }
    };

    return (
        <div className="container my-5">
            <div className="card">
                <div className="card-body">
                    <h2 className="card-title">Add Issue</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="vehicle" className="form-label">Vehicle Name</label>
                            <input
                                type="text"
                                id="vehicle"
                                className="form-control"
                                placeholder="Enter Vehicle Name"
                                value={issue.vehicle}
                                onChange={(e) => setIssue({ ...issue, vehicle: e.target.value })}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="component" className="form-label">Component Name</label>
                            <input
                                type="text"
                                id="component"
                                className="form-control"
                                placeholder="Enter Component Name"
                                value={issue.component}
                                onChange={(e) => setIssue({ ...issue, component: e.target.value })}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="quantity" className="form-label">Quantity</label>
                            <input
                                type="number"
                                id="quantity"
                                className="form-control"
                                value={issue.quantity}
                                onChange={(e) => setIssue({ ...issue, quantity: e.target.value })}
                                required
                                min="1"
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="type" className="form-label">Issue Type</label>
                            <select
                                id="type"
                                className="form-select"
                                value={issue.type}
                                onChange={(e) => setIssue({ ...issue, type: e.target.value })}
                            >
                                <option value="repair">Repair</option>
                                <option value="new">New Purchase</option>
                            </select>
                        </div>
                        <button type="submit" className="btn btn-primary">Add Issue</button>
                    </form>
                    {message && <div className="mt-3 alert alert-info">{message}</div>}
                </div>
            </div>
        </div>
    );
}

export default AddIssue;
