// components/AddIssue.js
import React, { useState } from 'react';

function AddIssue() {
    const [issue, setIssue] = useState({ vehicle: '', component: '', type: 'repair' });

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Issue Added:', issue);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Add Issue</h2>
            <input
                type="text"
                placeholder="Vehicle Name"
                value={issue.vehicle}
                onChange={(e) => setIssue({ ...issue, vehicle: e.target.value })}
                required
            />
            <input
                type="text"
                placeholder="Component Name"
                value={issue.component}
                onChange={(e) => setIssue({ ...issue, component: e.target.value })}
                required
            />
            <select onChange={(e) => setIssue({ ...issue, type: e.target.value })}>
                <option value="repair">Repair</option>
                <option value="new">New Purchase</option>
            </select>
            <button type="submit">Add Issue</button>
        </form>
    );
}

export default AddIssue;
