import React from 'react';

function Payment() {
    // Sample items - replace with dynamic data as needed
    const selectedItems = [
        { name: 'Brake Pads', quantity: 2, newPrice: 100, repairPrice: 50 },
        { name: 'Oil Filter', quantity: 1, newPrice: 30, repairPrice: 15 },
        { name: 'Battery', quantity: 1, newPrice: 120, repairPrice: 80 },
    ];

    // Function to calculate the total amount
    const calculateTotal = () => {
        return selectedItems.reduce((total, item) => {
            // Assuming we're calculating total for new purchases
            return total + (item.newPrice * item.quantity);
        }, 0);
    };

    const totalAmount = calculateTotal();

    const handlePayment = () => {
        alert(`Payment of $${totalAmount} completed!`);
    };

    return (
        <div className="container my-5">
            <div className="card">
                <div className="card-body">
                    <h2 className="card-title">Final Price Calculation</h2>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Item</th>
                                <th>Quantity</th>
                                <th>Price per Item</th>
                                <th>Total Price</th>
                            </tr>
                        </thead>
                        <tbody>
                            {selectedItems.map((item, index) => (
                                <tr key={index}>
                                    <td>{item.name}</td>
                                    <td>{item.quantity}</td>
                                    <td>${item.newPrice}</td>
                                    <td>${(item.newPrice * item.quantity).toFixed(2)}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <p className="fw-bold">Total Amount: <span className="text-success">${totalAmount.toFixed(2)}</span></p>
                    <button className="btn btn-primary" onClick={handlePayment}>Pay Now</button>
                </div>
            </div>
        </div>
    );
}

export default Payment;
