import React, { useState } from 'react';

function AddRepair() {
    const [repair, setRepair] = useState({ vehicle: '', description: '' });

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Repair Added:', repair);
        // Optionally reset the form after submission
        setRepair({ vehicle: '', description: '' });
    };

    return (
        <div className="container my-5">
            <div className="card">
                <div className="card-body">
                    <h2 className="card-title">Add Repair</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="vehicle" className="form-label">Vehicle Name</label>
                            <input
                                type="text"
                                id="vehicle"
                                className="form-control"
                                placeholder="Enter Vehicle Name"
                                value={repair.vehicle}
                                onChange={(e) => setRepair({ ...repair, vehicle: e.target.value })}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="description" className="form-label">Repair Description</label>
                            <textarea
                                id="description"
                                className="form-control"
                                placeholder="Enter Repair Description"
                                value={repair.description}
                                onChange={(e) => setRepair({ ...repair, description: e.target.value })}
                                required
                                rows="4" // Set a height for the textarea
                            />
                        </div>
                        <button type="submit" className="btn btn-primary">Add Repair</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default AddRepair;
