// components/RegisterComponent.js
import React, { useState } from 'react';

// Dummy data for components
const componentData = [
    { id: 1, name: 'Brake Pads', newPrice: 100, repairPrice: 50 },
    { id: 2, name: 'Oil Filter', newPrice: 30, repairPrice: 15 },
    { id: 3, name: 'Battery', newPrice: 120, repairPrice: 80 },
    { id: 4, name: 'Tire', newPrice: 150, repairPrice: 75 },
];

function RegisterComponent() {
    const [selectedComponent, setSelectedComponent] = useState('');
    const [customComponent, setCustomComponent] = useState({ name: '', type: 'new', price: 0 });
    const [componentType, setComponentType] = useState('new');

    const handleSubmit = (e) => {
        e.preventDefault();
        const componentToRegister = selectedComponent ? {
            name: selectedComponent.name,
            type: componentType,
            price: componentType === 'new' ? selectedComponent.newPrice : selectedComponent.repairPrice,
        } : customComponent;

        if (componentToRegister.price <= 0) {
            alert("Price must be a positive number.");
            return;
        }
        console.log('Component Registered:', componentToRegister);

        // Clear form after submission
        setSelectedComponent('');
        setCustomComponent({ name: '', type: 'new', price: 0 });
        setComponentType('new');
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Register Component</h2>
            <label>
                <input
                    type="radio"
                    value="existing"
                    checked={selectedComponent !== ''}
                    onChange={() => setSelectedComponent('')}
                />
                Select Existing Component
            </label>
            <select
                onChange={(e) => setSelectedComponent(componentData.find(comp => comp.id === parseInt(e.target.value)))}
                value={selectedComponent ? selectedComponent.id : ""}
            >
                <option value="" disabled>Select a component</option>
                {componentData.map(comp => (
                    <option key={comp.id} value={comp.id}>
                        {comp.name} - New: ${comp.newPrice} / Repair: ${comp.repairPrice}
                    </option>
                ))}
            </select>
            <br />
            <label>
                <input
                    type="radio"
                    value="custom"
                    checked={selectedComponent === ''}
                    onChange={() => setSelectedComponent(null)}
                />
                Add New Component
            </label>
            <input
                type="text"
                placeholder="Component Name"
                value={customComponent.name}
                onChange={(e) => setCustomComponent({ ...customComponent, name: e.target.value })}
                required={selectedComponent === ''}
            />
            <select
                value={componentType}
                onChange={(e) => {
                    setComponentType(e.target.value);
                    if (selectedComponent) {
                        setCustomComponent({ ...customComponent, price: componentType === 'new' ? selectedComponent.newPrice : selectedComponent.repairPrice });
                    }
                }}
            >
                <option value="new">New Purchase</option>
                <option value="repair">Repair</option>
            </select>
            <input
                type="number"
                placeholder="Price"
                value={selectedComponent ? (componentType === 'new' ? selectedComponent.newPrice : selectedComponent.repairPrice) : customComponent.price}
                onChange={(e) => setCustomComponent({ ...customComponent, price: +e.target.value })}
                required={selectedComponent === ''}
                min="0"
            />
            <button type="submit">Register</button>
        </form>
    );
}

export default RegisterComponent;
