import React, { useState } from 'react';
import axios from 'axios';

// Dummy data for components with newPrice and repairPrice
const componentData = [
    { id: 1, name: 'Brake Pads', newPrice: 100, repairPrice: 50 },
    { id: 2, name: 'Oil Filter', newPrice: 30, repairPrice: 15 },
    { id: 3, name: 'Battery', newPrice: 120, repairPrice: 80 },
    { id: 4, name: 'Tire', newPrice: 200, repairPrice: 100 },
];

function AddIssue() {
    const [issue, setIssue] = useState({ vehicle: '', component: '', type: 'repair', quantity: 1 });
    const [message, setMessage] = useState('');

    // Get selected component's details
    const selectedComponent = componentData.find(comp => comp.name === issue.component);

    // Determine price based on issue type and quantity
    const unitPrice = selectedComponent
        ? issue.type === 'repair'
            ? selectedComponent.repairPrice
            : selectedComponent.newPrice
        : 0;
    const totalPrice = unitPrice * issue.quantity;

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Replace the URL with your API endpoint
            const response = await axios.post('http://localhost:5000/api/issues', issue);
            console.log('Issue Added:', response.data);
            setMessage('Issue added successfully!');

            // Optionally reset the form after submission
            setIssue({ vehichalModal: '', component: '', type: 'repair', quantity: 1 });
        } catch (error) {
            console.error('There was an error adding the issue!', error);
            setMessage('Failed to add issue. Please try again.');
        }
    };

    return (
        <div className="container my-5">
            <div className="card">
                <div className="card-body">
                    <h2 className="card-title">Add Issue</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="vehichalModal" className="form-label">vehichalModal </label>
                            <input
                                type="text"
                                id="vehicle"
                                className="form-control"
                                placeholder="Enter Vehicle Name"
                                value={issue.vehicle}
                                onChange={(e) => setIssue({ ...issue, vehicle: e.target.value })}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="component" className="form-label">Component Name</label>
                            <select
                                id="component"
                                className="form-select"
                                value={issue.component}
                                onChange={(e) => setIssue({ ...issue, component: e.target.value })}
                                required
                            >
                                <option value="" disabled>Select Component</option>
                                {componentData.map(component => (
                                    <option key={component.id} value={component.name}>
                                        {component.name}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="type" className="form-label">Issue Type</label>
                            <select
                                id="type"
                                className="form-select"
                                value={issue.type}
                                onChange={(e) => setIssue({ ...issue, type: e.target.value })}
                            >
                                <option value="repair">Repair</option>
                                <option value="new">New Purchase</option>
                            </select>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="quantity" className="form-label">Quantity</label>
                            <input
                                type="number"
                                id="quantity"
                                className="form-control"
                                value={issue.quantity}
                                onChange={(e) => setIssue({ ...issue, quantity: parseInt(e.target.value) })}
                                min="1"
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Unit Price</label>
                            <input
                                type="text"
                                className="form-control"
                                value={unitPrice ? `$${unitPrice}` : 'Select a component'}
                                readOnly
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Total Price</label>
                            <input
                                type="text"
                                className="form-control"
                                value={`$${totalPrice.toFixed(2)}`}
                                readOnly
                            />
                        </div>
                        <button type="submit" className="btn btn-primary">Add Issue</button>
                    </form>
                    {message && <div className="mt-3 alert alert-info">{message}</div>}
                </div>
            </div>
        </div>
    );
}

export default AddIssue;
