// components/AddRepair.js
import React, { useState } from 'react';

function AddRepair() {
    const [repair, setRepair] = useState({ vehicle: '', description: '' });

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Repair Added:', repair);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Add Repair</h2>
            <input
                type="text"
                placeholder="Vehicle Name"
                value={repair.vehicle}
                onChange={(e) => setRepair({ ...repair, vehicle: e.target.value })}
                required
            />
            <textarea
                placeholder="Repair Description"
                value={repair.description}
                onChange={(e) => setRepair({ ...repair, description: e.target.value })}
                required
            />
            <button type="submit">Add Repair</button>
        </form>
    );
}

export default AddRepair;
