import React, { useState } from 'react';

function AddIssue() {
    const [issue, setIssue] = useState({ vehicle: '', component: '', type: 'repair' });

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Issue Added:', issue);
        // Optionally reset the form after submission
        setIssue({ vehicle: '', component: '', type: 'repair' });
    };

    return (
        <div className="container my-5">
            <div className="card">
                <div className="card-body">
                    <h2 className="card-title">Add Issue</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="vehicle" className="form-label">Vehicle Name</label>
                            <input
                                type="text"
                                id="vehicle"
                                className="form-control"
                                placeholder="Enter Vehicle Name"
                                value={issue.vehicle}
                                onChange={(e) => setIssue({ ...issue, vehicle: e.target.value })}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="component" className="form-label">Component Name</label>
                            <input
                                type="text"
                                id="component"
                                className="form-control"
                                placeholder="Enter Component Name"
                                value={issue.component}
                                onChange={(e) => setIssue({ ...issue, component: e.target.value })}
                                required
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="type" className="form-label">Issue Type</label>
                            <select
                                id="type"
                                className="form-select"
                                value={issue.type}
                                onChange={(e) => setIssue({ ...issue, type: e.target.value })}
                            >
                                <option value="repair">Repair</option>
                                <option value="new">New Purchase</option>
                            </select>
                        </div>
                        <button type="submit" className="btn btn-primary">Add Issue</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default AddIssue;
