// components/RegisterComponent.js
import React, { useState } from 'react';

function RegisterComponent() {
    const [component, setComponent] = useState({ name: '', type: 'new', price: 0 });

    const handleSubmit = (e) => {
        e.preventDefault();
        if (component.price <= 0) {
            alert("Price must be a positive number.");
            return;
        }
        console.log('Component Registered:', component);

        // Clear form after submission
        setComponent({ name: '', type: 'new', price: 0 });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Register Component</h2>
            <input
                type="text"
                placeholder="Component Name"
                value={component.name}
                onChange={(e) => setComponent({ ...component, name: e.target.value })}
                required
            />
            <select
                value={component.type}
                onChange={(e) => setComponent({ ...component, type: e.target.value })}
            >
                <option value="new">New Purchase</option>
                <option value="repair">Repair</option>
            </select>
            <input
                type="number"
                placeholder="Price"
                value={component.price}
                onChange={(e) => setComponent({ ...component, price: +e.target.value })}
                required
                min="0" // Optional for browsers that support it
            />
            <button type="submit">Register</button>
        </form>
    );
}

export default RegisterComponent;
