import React from 'react';

function Payment() {
    const totalAmount = 100; // Sample amount, replace with dynamic calculation

    const handlePayment = () => {
        alert(`Payment of $${totalAmount} completed!`);
    };

    return (
        <div className="container my-5">
            <div className="card">
                <div className="card-body">
                    <h2 className="card-title">Final Price Calculation</h2>
                    <p className="card-text">Total Amount: <span className="fw-bold">${totalAmount}</span></p>
                    <button className="btn btn-primary" onClick={handlePayment}>Pay Now</button>
                </div>
            </div>
        </div>
    );
}

export default Payment;
